    FILE *inputFile, *outputFile;
    char character;
    inputFile = fopen("input.txt", "r");
    if (inputFile == NULL) {
        printf("无法打开源文件！");
        return -1;
    }
    outputFile = fopen("output.txt", "w");
    if (outputFile == NULL) {
        fclose(inputFile);
        printf("无法创建目标文件！");
        return -1;
